import React, {useEffect, useState} from 'react';
import TinderCard from 'react-tinder-card';
import axios from "axios";
import '../assets/TinderCards.css';
import { createClient } from 'pexels';
import { Card } from 'react-bootstrap';



const options = {
  method: 'GET',
  url: 'https://dog.ceo/api/breeds/image/random/3'

};



//import response from 'response';

function TinderCards() {

    const [people, setPeople] = useState([]);  


    useEffect(() => {
        data();
    }, []);

    const data = async () => {

        try{
            const res = await axios.get('https://dog.ceo/api/breeds/image/random/3');
           // setPeople(res.data.message);
            console.log(res);


            let tmpArray = []
            for (var i = 0; i < res.data.message.length; i++) {
                tmpArray.push(res.data.message[i])
            }

            setPeople(tmpArray);
            console.log(tmpArray);
        }catch(err) {
            alert(err.message);
        }



    }








    const swiped = (direction, nameToDelete) => {
        console.log("removing: " + nameToDelete);
       // setLastDirection(direction);
    }
    const outOfFrame = (name) => {
        console.log(name + "left the screen");

    }

    const TinderCardArr = () => {
        for(let i = 0; i < people.length; i++) {
            <TinderCard
        className="swipe"
        key={i}
        preventSwipe={["up", "down"]}
        onSwipe={(dir) => swiped(dir, people.name)}
        onCardLeftScreen={() => outOfFrame(people.name)}
      >
        <div
          style={{ backgroundImage: `url(${people[0]})` }}
          className="card"
        >

        </div>
      </TinderCard>
        }

    }
    return (
        <div>


           {

          TinderCardArr(people)



        }
        </div>


    );
}


export default TinderCards;
