import React, {setState} from 'react';
import TinderCard from 'react-tinder-card';
import axios from "axios";
import '../assets/TinderCards.css';
import { createClient } from 'pexels';
import { Card } from 'react-bootstrap';






class TinderCards extends React.Component {
    constructor() {
        super();
    this.state = {
        people: []
    }  
this.swiped = this.swiped.bind(this);this.outOfFrame = this.outOfFrame.bind(this);
}

 componentDidMount(){
     const options = {
  method: 'GET',
  url: 'https://dog.ceo/api/breeds/image/random/3'

};
             const data = axios.request(options).then(function (response) {
            console.log(response.data);
            setState({people: response.data});

        }).catch(function (error) {
            console.error(error);
        });
        }

         swiped = (direction, nameToDelete) => {
            console.log("removing: " + nameToDelete);
           // setLastDirection(direction);
        }
        outOfFrame = (name) => {
            console.log(name + "left the screen");

        }

    render() {










        return (
            <div>


                 {this.state.people.map((person) => (
              <TinderCard
                className="swipe"
                key={person.name}
                preventSwipe={["up", "down"]}
                onSwipe={(dir) => this.swiped(dir, person.name)}
                onCardLeftScreen={() => this.outOfFrame(person.name)}
              >
                <div
                  style={{  backgroundImage: "url(" + "https://www.google.com/search?q=dog+images&tbm=isch&source=iu&ictx=1&fir=wzRcY9R2ANhK-M%252C2r6Arj4-hBjhNM%252C_&vet=1&usg=AI4_-kTLa0YJSehgPBHBaNYpKniW-AB3JQ&sa=X&ved=2ahUKEwic0NTErYnxAhUqhuAKHaXqDrsQ9QF6BAgPEAE#imgrc=wzRcY9R2ANhK-M" + ")",}}
                  className="card"
                >
                  <h3>{person.name}</h3>
                </div>
              </TinderCard>
            ))}
            </div>


        );
    }
}
export default TinderCards;
