import React, {useEffect, useState} from 'react';
import TinderCard from 'react-tinder-card';
import axios from "axios";
import '../assets/TinderCards.css';
import { createClient } from 'pexels';
import { Card } from 'react-bootstrap';

const client = createClient('563492ad6f91700001000001733b3847b1a34e68b77f3a927216787e');

//import response from 'response';

function TinderCards() {
    const [search, setSearch] = useState("");  
    const [perPage, setPerPage] = useState("");  
    const [result, setResult] = useState([]);  
    const [people, setPeople] = useState([]);  

    function handleChange(event) {  
        const search = event.target.value;  
        setSearch(search);  
    }  
    function noOfPics(event) {  
        const perPage = event.target.value;  
        setPerPage(perPage);  
    }  

    function handleSubmit(event) {  
        event.preventDefault();  
        const url = "https://api.pexels.com/v1/search?query=people&per_page=" + 1;  
        const access_token = '563492ad6f917000010000014060d806c66c47b88b9b4d7f8c487692';  
        axios.get(url, {  
            headers: {  
                'Authorization': `${access_token}`  
            }  
        }).then(resp => {  

            setPeople(resp.data.photos);  


        })  

    }  




    const swiped = (direction, nameToDelete) => {
        console.log("removing: " + nameToDelete);
       // setLastDirection(direction);
    }
    const outOfFrame = (name) => {
        console.log(name + "left the screen");

    }
    return (
        <div>
              { people.map((person) => {
         return (
<TinderCard
            className="swipe"
            key={person.name}
            preventSwipe={["up", "down"]}
            onSwipe={(dir) => swiped(dir, person.name)}
            onCardLeftScreen={() => outOfFrame(person.name)}
          >
            <div
              style={{ backgroundImage: `url(${person[0]})` }}
              className="card"
            >
              <h3>{person.name}</h3>
            </div>
          </TinderCard>

              )})}
        </div>


    );
}

export default TinderCards;
