import React, {setState} from 'react';
import TinderCard from 'react-tinder-card';
import axios from "axios";
import '../assets/TinderCards.css';
import { createClient } from 'pexels';
import { Card } from 'react-bootstrap';






class TinderCards extends React.Component {
    constructor(props) {
        super(props);
    this.state = {
        people: [],
        isLoaded: false
    }  
this.swiped = this.swiped.bind(this);this.outOfFrame = this.outOfFrame.bind(this);
}




        componentDidMount() {
            axios.interceptors.response.use((response) => {
                // do something with the response data
                console.log('Response was received');

                return response;
              }, error => {
                // handle the response error
                return Promise.reject(error);
              });

              // sent a GET request
              axios.get('https://api.github.com/users/mapbox')
              .then(res =>   
               this.setState({
                  isLoaded: true,
                  people: res.data.result
                }),
                  console.log(this.state.people));



            }

         swiped = (direction, nameToDelete) => {
            console.log("removing: " + nameToDelete);
           // setLastDirection(direction);
        }
        outOfFrame = (name) => {
            console.log(name + "left the screen");

        }

    render() {










        return (
            <div>


                 {this.state.isLoaded && this.state.people.map((person) => (
              <TinderCard
                className="swipe"
                key={person.name}
                preventSwipe={["up", "down"]}
                onSwipe={(dir) => this.swiped(dir, person.name)}
                onCardLeftScreen={() => this.outOfFrame(person.name)}
              >
                <div
                  style={{  backgroundImage: "url(" + this.state.people.url + ")",}}
                  className="card"
                >
                  <h3>{person.name}</h3>
                </div>
              </TinderCard>
            ))}
            </div>


        );
    }
}
export default TinderCards;
