import React, {useEffect, useState} from 'react';
import TinderCard from 'react-tinder-card';
import axios from "axios";
import '../assets/TinderCards.css';
import { createClient } from 'pexels';
import { Card } from 'react-bootstrap';

const client = createClient('563492ad6f91700001000001733b3847b1a34e68b77f3a927216787e');

//import response from 'response';

function TinderCards() {
    const [search, setSearch] = useState("");  
    const [perPage, setPerPage] = useState("");  
    const [result, setResult] = useState([]);  
    const [peope, setPeople] = useState([]);  

    function handleChange(event) {  
        const search = event.target.value;  
        setSearch(search);  
    }  
    function noOfPics(event) {  
        const perPage = event.target.value;  
        setPerPage(perPage);  
    }  

    function handleSubmit(event) {  
        event.preventDefault();  
        const url = "https://api.pexels.com/v1/search?query=" + people + "&per_page=" + 1;  
        const access_token = '563492ad6f917000010000014060d806c66c47b88b9b4d7f8c487692';  
        axios.get(url, {  
            headers: {  
                'Authorization': `${access_token}`  
            }  
        }).then(data => {  
            console.log(data);  
            setPeople(data.data.photos);  
        })  

    }  




    const swiped = (direction, nameToDelete) => {
        console.log("removing: " + nameToDelete);
       // setLastDirection(direction);
    }
    const outOfFrame = (name) => {
        console.log(name + "left the screen");

    }
    return (
        <form onSubmit={handleSubmit}>  
        <div className="card-header main-search">  
            <div className="row">  
                <div className="col-12 col-md-3 col-xl-3">  
                    <input onChange={handleChange} className="AutoFocus form-control" placeholder="Type something..." type="text" />  
                </div>  
                <div className="col-12 col-md-3 col-xl-3">  
                    <input onChange={noOfPics} name="deliveryNumber" className="AutoFocus form-control" placeholder="No of Images"  
                        type="text" />  
                </div>  
                <div className="ml-auto">  
                    <input type="submit" value="Search" className="btn btn-primary search-btn" />  
                </div>  
            </div>  
        </div>  
        <div class="container">  
            <div class="row">  
                {result.map(search => (  
                    <div className="col-sm-4">  
                        <Card style={{ 'margin-top': '10px' }}>  
                            <Card.Img variant="top" src={search.src.landscape} alt={search.photographer} />  
                            <Card.Body>  
                                <h5 className="card-title">Card title</h5>  
                                <a className="btn btn-primary">Know more</a>  
                            </Card.Body>  
                        </Card>  
                    </div>  
                ))}  
            </div>  
        </div>  
    </form>  
    );
}

export default TinderCards;
